import {
  logger,
  optimismWallet,
  RECEIVER_ADDRESS,
} from "../utils/common";
import { ethers } from "ethers";

const log = logger.getLogger("sign and broadcast");
async function signMessage(data: string) {
  try {
    const signedMessage = await optimismWallet.signMessage(
      JSON.stringify(data)
    );
    log.info("Signed message:", signedMessage);
    return signedMessage;
  } catch (error) {
    log.error("Error signing message");
    log.debug(error);
    throw error;
  }
}
async function main() {
  try {
    const dataToSign =
      "";

    const signedMessage = await signMessage(dataToSign);

    const broadcastResult = await broadcastSignedMessage(
      signedMessage
    );

    if (broadcastResult) {
      log.info("Successfully broadcast signed data to Optimism");
      log.debug("Broadcast result:", broadcastResult);
      log.info("Transaction hash:", broadcastResult.hash);
      log.info("Check transaction at: https://optimistic.etherscan.io/tx/" + broadcastResult.hash);
    } else {
      throw new Error("Failed to broadcast signed message");
    }
  } catch (error) {
    log.error("Failed to sign and broadcast");
    log.debug(error);
  }
}

main().catch((err) => {
  log.error("There was an error in the main function");
  log.debug(err);
});


async function broadcastSignedMessage(signedMessage: string) {
  try {
    const tx = {
      to: RECEIVER_ADDRESS,
      data: signedMessage,
      value: ethers.parseEther("0.001"),
    };

    const transactionResponse = await optimismWallet.sendTransaction(tx);
    await transactionResponse.wait();
    return transactionResponse;
  } catch (error) {
    log.error("Error broadcasting signed message");
    log.debug(error);
    return null;
  }
}
